-- Банк фрагментов для генерации психологических портретов

-- Фрагменты для шкалы ТРЕВОЖНОСТЬ (anxiety)
INSERT OR IGNORE INTO personality_fragments (scale_id, weight_range, fragment_type, title, content, emotional_impact, order_priority) VALUES 

-- Высокая тревожность
(1, 'high', 'behavior', 'Твоя тревога - это твоя суперсила', 'Да, ты из тех, кто накручивает себя до состояния "а что если...". Твой мозг постоянно сканирует опасности, даже там, где их нет. Но знаешь что? Эта же тревожность делает тебя невероятно эмпатичным и внимательным к деталям, которые другие пропускают.', 5, 1),

(1, 'high', 'weakness', 'Твоя тревога съедает тебя изнутри', 'Ты живёшь в постоянном напряжении. Каждое непрочитанное сообщение превращается в катастрофу, каждая задержка друга - в трагедию. Твой мозг рисует самые мрачные сценарии, и ты веришь им больше, чем реальности.', 5, 2),

(1, 'high', 'advice', 'Как приручить свою тревогу', 'Твоя тревога - не враг, а перевозбуждённый охранник. Начни записывать свои страхи, а потом проверяй, сколько из них сбылось. Спойлер: почти ни один. Дыхательные практики и физическая активность - твои лучшие друзья в борьбе с тревожными мыслями.', 4, 3),

-- Средняя тревожность
(1, 'medium', 'behavior', 'Ты держишь тревогу под контролем', 'У тебя здоровый уровень осторожности. Ты можешь волноваться перед важными событиями, но не даёшь тревоге парализовать себя. Это помогает тебе быть готовым к различным ситуациям, не теряя при этом способности наслаждаться жизнью.', 3, 1),

-- Низкая тревожность
(1, 'low', 'strength', 'Твоё спокойствие - твоя сила', 'Ты один из тех редких людей, которые умеют оставаться спокойными в хаосе. Твоя эмоциональная стабильность притягивает других, они чувствуют себя в безопасности рядом с тобой. Ты не накручиваешь себя по пустякам и умеешь видеть ситуацию объективно.', 4, 1),

(1, 'low', 'weakness', 'Иногда твоё спокойствие играет против тебя', 'Твоя низкая тревожность может сделать тебя слишком расслабленным в ситуациях, которые требуют осторожности. Ты можешь недооценивать риски или не замечать тревожные сигналы, которые очевидны для других.', 3, 2),

-- Фрагменты для шкалы САМООЦЕНКА (self_worth)
-- Низкая самооценка
(2, 'low', 'behavior', 'Ты не веришь в свою ценность', 'Внутри тебя живёт жестокий критик, который постоянно твердит, что ты недостаточно хорош. Ты обесцениваешь свои достижения и преувеличиваешь неудачи. Каждый комплимент кажется тебе ложью, а каждая критика - горькой правдой.', 5, 1),

(2, 'low', 'weakness', 'Твоя самооценка разрушает твои отношения', 'Ты постоянно ищешь подтверждения своей ценности у других, но даже получив его, не веришь. Ты можешь принижать себя, чтобы другие тебя "успокоили", или наоборот - агрессивно защищаться от мнимых нападок.', 5, 2),

(2, 'low', 'advice', 'Как полюбить себя по-настоящему', 'Начни записывать каждый день хотя бы одну вещь, которую ты сделал хорошо. Перестань сравнивать свою внутреннюю борьбу с чужими идеальными фасадами в соцсетях. Твоя ценность не зависит от чужого мнения - это базовый факт твоего существования.', 4, 3),

-- Высокая самооценка
(2, 'high', 'strength', 'Ты знаешь себе цену', 'У тебя здоровая самооценка - ты понимаешь свои сильные стороны, но не игнорируешь области для роста. Ты не нуждаешься в постоянном одобрении других, потому что твоя ценность исходит изнутри. Это делает тебя привлекательным и надёжным партнёром.', 4, 1),

-- Фрагменты для шкалы СОЗАВИСИМОСТЬ (codependency)
-- Высокая созависимость
(3, 'high', 'behavior', 'Ты растворяешься в других людях', 'Твоё счастье полностью зависит от настроения и состояния близких. Ты чувствуешь себя ответственным за чужие эмоции и готов пожертвовать собой, лишь бы другим было хорошо. Твои границы настолько размыты, что ты забыл, где заканчиваешься ты и начинается другой человек.', 5, 1),

(3, 'high', 'weakness', 'Твоя созависимость токсична', 'Ты притягиваешь людей, которые используют твою готовность жертвовать собой. Ты можешь годами находиться в разрушительных отношениях, думая, что "спасаешь" партнёра. На самом деле ты часто мешаешь людям взрослеть, решая за них их проблемы.', 5, 2),

(3, 'high', 'advice', 'Как найти себя в отношениях', 'Начни с простого: научись говорить "нет", не объясняя причин. Твоё время, энергия и эмоции - это ресурсы, которыми ты имеешь право распоряжаться. Помни: ты не можешь спасти того, кто не хочет спасаться.', 4, 3),

-- Низкая созависимость
(3, 'low', 'strength', 'Ты умеешь любить, сохраняя себя', 'У тебя здоровые границы в отношениях. Ты можешь быть близким с людьми, не теряя при этом собственной идентичности. Ты понимаешь разницу между поддержкой и спасательством, между любовью и контролем.', 4, 1),

-- Фрагменты для шкалы УВЕРЕННОСТЬ (confidence)
-- Низкая уверенность
(4, 'low', 'behavior', 'Ты сомневаешься в каждом своём шаге', 'Принятие решений превращается для тебя в пытку. Ты можешь часами мучиться выбором, боясь сделать неправильный шаг. Даже когда всё складывается хорошо, ты думаешь, что это случайность, а не результат твоих усилий.', 4, 1),

(4, 'low', 'advice', 'Как обрести уверенность в себе', 'Начни с маленьких решений и придерживайся их. Веди дневник своих "побед" - даже самых мелких. Помни: уверенность строится через действие, а не через размышления. Каждый раз, когда ты делаешь то, чего боишься, ты становишься сильнее.', 4, 2),

-- Высокая уверенность
(4, 'high', 'strength', 'Твоя уверенность вдохновляет', 'Ты умеешь принимать решения и нести за них ответственность. Люди тянутся к тебе, потому что чувствуют твою внутреннюю силу. Ты не боишься ошибок, потому что знаешь - это часть пути к успеху.', 4, 1),

-- Фрагменты для шкалы ОБИДЧИВОСТЬ (resentment)
-- Высокая обидчивость
(5, 'high', 'behavior', 'Ты коллекционируешь обиды', 'Твоя память отлично запоминает каждое неосторожное слово, каждый взгляд, каждое невнимание. Ты можешь годами носить в себе обиду, периодически доставая её и переживая заново. Твоё сердце превратилось в музей болезненных воспоминаний.', 5, 1),

(5, 'high', 'weakness', 'Твои обиды отравляют твою жизнь', 'Ты можешь неделями не разговаривать с близкими из-за одного неловкого момента. Твоя обидчивость делает общение с тобой похожим на ходьбу по минному полю - никто не знает, что может тебя задеть.', 5, 2),

(5, 'high', 'advice', 'Как освободиться от груза обид', 'Прощение - это не оправдание чужих поступков, а освобождение себя от боли. Попробуй технику "письма обидчику" - напиши всё, что чувствуешь, а потом сожги письмо. Твоё сердце создано для любви, а не для хранения боли.', 4, 3),

-- Фрагменты для шкалы САМОСАБОТАЖ (self_sabotage)
-- Высокий самосаботаж
(6, 'high', 'behavior', 'Ты сам себе худший враг', 'Когда жизнь начинает налаживаться, ты находишь способ всё разрушить. Ты боишься успеха больше, чем неудачи, потому что неудача привычна и понятна. Твоё подсознание убеждено, что ты не заслуживаешь хорошего.', 5, 1),

(6, 'high', 'weakness', 'Твой самосаботаж разрушает твои мечты', 'Ты можешь бросить работу мечты из-за страха не справиться. Разрушить отношения, когда они становятся слишком серьёзными. Твоя зона комфорта стала тюрьмой, из которой ты боишься выйти.', 5, 2),

(6, 'high', 'advice', 'Как перестать воевать с собой', 'Признай свои страхи и поговори с ними как с испуганными детьми. Когда поймаешь себя на самосаботаже, остановись и спроси: "Чего я боюсь?". Начни с маленьких побед и постепенно расширяй зону комфорта.', 4, 3),

-- Фрагменты для шкалы КОНТРОЛЬ/ДОВЕРИЕ (control_trust)
-- Высокая потребность в контроле
(7, 'high', 'behavior', 'Ты хочешь контролировать всё и всех', 'Ты планируешь каждую минуту, предугадываешь каждый сценарий. Неопределённость вызывает у тебя панику, а спонтанность других людей - раздражение. Ты веришь, что если не будешь всё контролировать, то случится катастрофа.', 4, 1),

(7, 'high', 'weakness', 'Твоя потребность в контроле душит отношения', 'Ты можешь превратить партнёра в проект для улучшения. Твоя любовь часто выглядит как попытка переделать человека под свои стандарты. Люди рядом с тобой чувствуют себя объектами, а не личностями.', 5, 2),

-- Высокое доверие (низкая потребность в контроле)
(7, 'low', 'strength', 'Ты умеешь доверять жизни', 'У тебя потрясающая способность отпускать ситуации и доверять процессу. Ты понимаешь, что не можешь контролировать всё, и это тебя не пугает. Твоё доверие к жизни и людям создаёт вокруг тебя атмосферу лёгкости и свободы.', 4, 1),

-- Фрагменты для шкалы ЭМОЦИОНАЛЬНАЯ СТАБИЛЬНОСТЬ (emotional_stability)
-- Низкая эмоциональная стабильность
(8, 'low', 'behavior', 'Твои эмоции - американские горки', 'Ты можешь быть на седьмом небе утром и в глубокой депрессии вечером. Твоё настроение зависит от тысячи мелочей: погоды, взгляда прохожего, тона сообщения. Люди рядом с тобой никогда не знают, какого тебя они встретят сегодня.', 4, 1),

(8, 'low', 'weakness', 'Твоя эмоциональность истощает тебя и других', 'Ты живёшь на эмоциональных качелях, что отнимает огромное количество энергии. Твоя непредсказуемость может пугать близких, они не знают, как с тобой взаимодействовать.', 4, 2),

(8, 'low', 'advice', 'Как стабилизировать свои эмоции', 'Начни отслеживать триггеры, которые выбивают тебя из колеи. Веди дневник настроения, ищи закономерности. Практикуй техники заземления: дыхание, медитация, физические упражнения. Помни: ты не твои эмоции, ты тот, кто их наблюдает.', 4, 3),

-- Высокая эмоциональная стабильность
(8, 'high', 'strength', 'Ты - скала в океане эмоций', 'У тебя удивительная способность сохранять эмоциональное равновесие даже в самых стрессовых ситуациях. Люди тянутся к тебе за поддержкой, потому что чувствуют твою внутреннюю силу. Ты умеешь быть якорем стабильности для себя и других.', 4, 1);

-- Дополнительные универсальные фрагменты для финального портрета
INSERT OR IGNORE INTO personality_fragments (scale_id, weight_range, fragment_type, title, content, emotional_impact, order_priority) VALUES 

-- Универсальные заключительные фрагменты
(NULL, 'universal', 'conclusion', 'Твой путь к себе', 'Помни: твоя личность - не приговор, а отправная точка для роста. Каждая твоя особенность может стать как силой, так и слабостью - всё зависит от того, как ты с ней работаешь. Ты уже сделал первый шаг, честно ответив на вопросы о себе.', 3, 1),

(NULL, 'universal', 'motivation', 'Ты сильнее, чем думаешь', 'Внутри тебя есть всё необходимое для счастливой жизни. Да, возможно, некоторые паттерны нужно изменить, но ты уже на пути к этому. Каждый день - это новая возможность стать лучшей версией себя.', 4, 1),

(NULL, 'universal', 'call_to_action', 'Что дальше?', 'Самопознание - это не разовая акция, а постоянный процесс. Возвращайся к этому портрету через несколько месяцев и сравни, что изменилось. Делись своими открытиями с близкими - часто они видят в нас то, что мы сами не замечаем.', 3, 2);